import { useState } from 'react'
import './Timmer.css'
export default function Timmer() {
    const [sec, setSec] = useState(0);
    function handleClick() {
        while (true) {
            setSec(sec => sec + 1)
        }

    }
    return (
        <div className="timmer-container border p-4">
            <h3><span>10</span>:<span>12</span>:<span>12</span></h3>
            <div>
                <button className='btn btn-primary' onClick={handleClick}>Start</button>
                <button className='btn btn-success'>Reset</button>
            </div>
        </div>
    )
}
